mapper.select:
select auth_url
from sys_authority
where auth_id = #{authId,jdbcType=VARCHAR}

SELECT
C.auth_id, C.auth_parent_id, C.auth_type, C.auth_text, C.auth_url, C.auth_tip, C.auth_image
from sys_user as A
LEFT JOIN user_role as Z ON Z.user_id = A.user_id
LEFT JOIN sys_role as B ON B.role_id = Z.role_id
LEFT JOIN role_authority as Y ON Y.role_id = B.role_id
LEFT JOIN sys_authority as C ON C.auth_id = Y.auth_id
WHERE A.user_name = #{userName, jdbcType=VARCHAR}


  AND C.auth_parent_id = #{authParentId, jdbcType=VARCHAR}



  AND C.auth_type = #{authType, jdbcType=VARCHAR}

GROUP BY C.auth_id;

mapper.insert:
insert into sys_authority (auth_id, auth_parent_id, auth_type, 
  auth_text, auth_url, auth_tip
  )
values (#{authId,jdbcType=VARCHAR}, #{authParentId,jdbcType=VARCHAR}, #{authType,jdbcType=VARCHAR}, 
  #{authText,jdbcType=VARCHAR}, #{authUrl,jdbcType=VARCHAR}, #{authTip,jdbcType=VARCHAR}
  )
